import React, { Component } from 'react';
import NameForms from './NameForm';
import EmailForms from './EmailForms';
import CpfForms from './CpfForms';
import EnderecoForms from './EnderecoForms';
import CidadeForms from './CidadeForms';

class Form extends Component {
  constructor() {
    super();

    this.state = {
      name: '',
      email: '',
      cpf: '',
      endereco: '',
      cidade: '',
      estado: '',
      tipo: 'casa'
    }
  }

  handleEvent = (event) => {
    const {name, value} = event.target;
    this.setState({
      [name] : value
    })
  }


  render() {
    const { name, email, cpf, endereco, cidade, estado, tipo} = this.state;

    return (
      <div>
        <fieldset className="data-fieldset">
          <legend>Dados Pessoais</legend>
          <NameForms/>
          <EmailForms/>
          <CpfForms/>
          <EnderecoForms/>
          <CidadeForms/>






          <label>
            Estado:
            <select 
            name="estado" 
            type=""
            required
            value={endereco}
            onChange={this.handleEvent}
            maxLength={200}
            />
          </label>

          <label>
            Tipo:
            <input 
            name="tipo" 
            type="radio" 
            required
            value={tipo}
            onChange={this.handleEvent}
            />
          </label>

        </fieldset>
      </div>
    );
  }
}

export default Form;
