import React, { Component } from 'react';

class Form extends Component {
  constructor() {
    super();

    this.state = {
      name: '',
      email: '',
      cpf: ''
    }
  }

  handleEvent = (event) => {
    const {name, value} = event.target;
    this.setState({
      [name] : value
    })
  }


  render() {
    const { name, email, cpf} = this.state;

    return (
      <div>
        <fieldset className="data-fieldset">
          <legend>Dados Pessoais</legend>
          <label>
            Nome:
            <input 
            name="name" 
            type="text" 
            required
            value={name.toUpperCase()}
            onChange={this.handleEvent}
            maxLength={40}
            />
          </label>
          <label>
            Email
            <input 
            name="email" 
            type="email" 
            required
            value={email}
            onChange={this.handleEvent}
            />
          </label>

          <label>
            CPF
            <input 
            name="cpf" 
            type="number" 
            required
            value={cpf}
            onChange={this.handleEvent}
            max={99999999999}
            />
          </label>

          <label>
            CPF
            <input 
            name="cpf" 
            type="number" 
            required
            value={cpf}
            onChange={this.handleEvent}
            maxLength={11}
            />
          </label>

        </fieldset>
      </div>
    );
  }
}

export default Form;
