import React, { Component } from 'react';
import Marker from 'pigeon-marker';
import Map from 'pigeon-maps';

import { connect } from 'react-redux';
import Coordinates from './Coordinates';
import { getCurrentISSLocation } from '../services/issAPI';
import { getISSLocationSuccess as getISSLocationSuccessAction } from '../actions';

class ISSLocation extends Component {
  componentDidMount() {
    const { getISSLocationSuccess } = this.props;
    getCurrentISSLocation().then((response) => {
      const payload = {
        latitude: response.iss_position.latitude,
        longitude: response.iss_position.longitude,
      };
      getISSLocationSuccess(payload);
    });
  }

  render() {
    const { latitude, longitude } = this.props;

    return (
      <main>
        <div className="map">
          <Map
            center={ [0, 0] }
            defaultWidth={ 450 }
            height={ 450 }
            minZoom={ 1.5 }
            maxZoom={ 8 }
            zoom={ 1.5 }
          >
            <Marker anchor={ [latitude, longitude] } />
          </Map>
        </div>
        <Coordinates latitude={ latitude } longitude={ longitude } />
      </main>
    );
  }
}

const mapStateToProps = (state) => ({
  latitude: state.issLocation.latitude,
  longitude: state.issLocation.longitude,
});

const mapDispatchToProps = (dispatch) => ({
  getISSLocationSuccess: (payload) => dispatch(getISSLocationSuccessAction(payload)),
});

export default connect(mapStateToProps, mapDispatchToProps)(ISSLocation);
