import React, { Component, createContext } from 'react';
import './App.css';
import ISSLocation from './components/ISSLocation';

const ISSContext = createContext();

class App extends Component {
  constructor() {
    super();
    this.state = {
      latitude: 10,
      longitude: 40,
      error: null,
      isLoading: false,
    };
  }

  render() {
    return (
      <ISSContext.Provider value={ { ...this.state } }>
        <div className="App">
          <h1>
            Space Station
            {' '}
            <span className="purple-font">Tracker</span>
          </h1>
          <ISSLocation />
        </div>
      </ISSContext.Provider>
    );
  }
}

export default App;
